import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { FaBars } from 'react-icons/fa';
import './ReviewsPage.css';
import logoImg from '../assets/logo-urbanovo.png';
import reviewImg1 from '../../assets/colorful-flower-garden.jpg';
import reviewImg2 from '../../assets/closeup-shot-female-using-paint-rollers-with-color-blue.jpg';
import reviewImg3 from '../../assets/tiler-working-renovation-apartment.jpg';

const reviews = [
  {
    id: 1,
    name: 'Elena',
    title: '¡Encantada con el resultado!',
    text: 'Gracias a Urbanovo, mi jardín parece nuevo. ¡Súper profesionales y detallistas!',
    image: reviewImg1,
  },
  {
    id: 2,
    name: 'Luis',
    title: 'Equipo muy eficiente',
    text: 'Pintaron la fachada y arreglaron detalles que otros ignoraron. ¡10/10!',
    image: reviewImg2,
  },
  {
    id: 3,
    name: 'María',
    title: 'Transformaron mi patio',
    text: 'Ahora mi patio es funcional y bonito. ¡Recomiendo Urbanovo sin dudar!',
    image: reviewImg3,
  }
];

const ReviewsPage = () => {
  const [menuOpen, setMenuOpen] = useState(false);

  return (
    <section className="reviews-page">
      <nav className="hero-menu">
        <Link to="/" className="logo">
          <img src={logoImg} alt="Urbanovo" className="logo-img" />
        </Link>

        <div className="menu-links">
          <Link to="/servicios">Servicios</Link>
          <Link to="/contacto">Contacto</Link>
          <Link to="/resenyas">Reseñas</Link>
        </div>
        <FaBars className="burger-menu" onClick={() => setMenuOpen(!menuOpen)} />
        {menuOpen && (
          <div className="menu-mobile">
            <Link to="/servicios" onClick={() => setMenuOpen(false)}>Servicios</Link>
            <Link to="/contacto" onClick={() => setMenuOpen(false)}>Contacto</Link>
            <Link to="/resenyas" onClick={() => setMenuOpen(false)}>Reseñas</Link>
          </div>
        )}
      </nav>

      <div className="reviews-section">
        <h2 className="reviews-title">¿Qué dicen nuestros clientes?</h2>
        <div className="reviews-grid">
          {reviews.map((review) => (
            <div className="review-card" key={review.id}>
              <img src={review.image} alt={review.name} className="review-avatar" />
              <h3>{review.title}</h3>
              <p className="review-text">{review.text}</p>
              <p className="review-name">{review.name}</p>
            </div>
          ))}
        </div>
        <button className="reviews-button">Ver todos los comentarios</button>
      </div>
    </section>
  );
};

export default ReviewsPage;
